import { Namespace } from "./index.js";
export class BrowserItem extends Namespace {
    raw;
    constructor(ableton, raw) {
        super(ableton, "browser-item", raw.id);
        this.raw = raw;
        this.transformers = {
            children: (children) => children.map((c) => new BrowserItem(ableton, c)),
        };
        this.cachedProps = {
            children: true,
            is_device: true,
            is_folder: true,
            is_loadable: false,
            is_selected: false,
            name: true,
            source: true,
            uri: true,
        };
    }
}
